import React,{useState} from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar } from '@fortawesome/free-solid-svg-icons'

const Rate =()=> {
    const[stars,setStars]=useState(()=>{return [false,false,false,false,false]})

    const rate=(i)=> {
        setStars((prevState)=>{
            return []
        })
    }

    return (
        <div className="text-center">
            {
                stars.map((Star,i)=> <FontAwesomeIcon key={i} size="lg" icon={faStar} onClick={()=>rate(i++)}/>)
            }
        </div>
    )
}

export default Rate
