import React,{useState} from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar } from '@fortawesome/free-solid-svg-icons'

const Rate =()=> {
    const[stars,setStars]=useState([false,false,false,false,false])
    const [note,setNote]=useState(0)

    const rate=(i)=>{
        if(i!==0&&stars===[true,false,false,false,false]){
            setStars([false,false,false,false,false])
        }else{
            let x= stars.map((el,x)=> (x<=i || el[i]===false)? el=true:el=false);
            setStars(x);
            const y = stars.filter((el)=>el===true).length
            setNote(note + y)
            console.log(note+y)

        }

    }





    return (
        <div >
            {
                stars.map((Star,i)=> <FontAwesomeIcon key={i} color={stars[i]===true? "yellow":"gray"} size="lg" icon={faStar} onClick={()=> rate(i)} />)
            }
        </div>
    )
}

export default Rate
