import React,{useState} from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar } from '@fortawesome/free-solid-svg-icons'

const Rate =()=> {
    const[stars,setStars]=useState(()=>{return [false,false,false,false,false]})

    const change=(i)=>{
        setStars([false,false,false,false,false])
        let note= stars.map((el,x)=> x<=i? el=true:el)
        setStars(note);

    }




    return (
        <div className="text-center">
            {
                stars.map((Star,i)=> <FontAwesomeIcon key={i} color={stars[i]===true? "yellow":"gray"} size="lg" icon={faStar} onClick={()=> change(i)} />)
            }
        </div>
    )
}

export default Rate
