import React,{useState} from 'react'
import Rate from '../Rate Component/Rate'
import Note from '../Note Component/Note'

function MovieCard(props) {
    const [toggle,setToggle]=useState(false)
    const handlerRate=()=>{
        setToggle(true)
    }
    return (

        <div className="col mb-2">
            <div className="card bg-dark w-100 h-100" >
                <img src={props.movie.posterUrl} className="card-img-top" alt="..." />
                <div className="card-body" style={{height:"70px"}}>
                    <h5 className="card-title text-wrap text-center mt-1" style={{fontSize:"17px"}}>{props.movie.title}</h5>
                </div>
                <div className="card-footer" style={{height:"45px"}} onDoubleClick={handlerRate}>
                    <small className="text-muted"><Rate/></small>
                    <button type="button" class="btn btn-primary">
                        Notifications <span class="badge badge-light">4</span>
                    </button>
                </div>

            </div>
        </div>
    )
};
export default MovieCard;
