import React from 'react';
import './Details.css';

export default function Details() {
  return (
    <div
      className="details-container px-5"
      style={{ background: 'red', width: '40%' }}
    >
      <div className="movie-container m-2 ">
        <img
          src={props.movie.posterUrl}
          className="card-img-top"
          alt="..."
          style={{ height: '75%' }}
          onClick={() => showAlert()}
        />
        <div className="card-body">
          <h5
            className="card-title text-wrap text-center"
            style={{ fontSize: '17px' }}
          >
            {props.movie.title.toUpperCase()}
          </h5>
        </div>
        <div
          className="card-footer d-flex justify-content-between align-items-center"
          style={{ height: '60px' }}
        >
          <small className="text-muted">
            {stars.map((star, i) => (
              <FontAwesomeIcon
                key={i}
                color={star ? 'yellow' : 'gray'}
                size="lg"
                icon={faStar}
                onClick={() => {
                  props.rate(props.movie.id, i, [stars, setStars]);
                  handleCounteur();
                }}
              />
            ))}
          </small>

        </div>
      </div>
    </div>
  );
}
