import React, { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faFilter,faStar, faCheck} from '@fortawesome/free-solid-svg-icons'


import './App.css';
import { moviesList } from './Components/Input.js'
import Filter from './Components/Filter Component/FilterComponent'
import MoviesList from './Components/List Movies Component/MoviesList'
import Modal from './Components/Add Movie Component/AddMovie'



function App(props) {



  const [movies, setMovies] = useState(moviesList)
  const [add, setAdd] = useState(() => { return false })
  const AddMovieDetail = () => {
    setAdd(prevState => !prevState)
  }
  // add Movie Details
  const saveDetailsMovie = (title, description, url) => {

    const newMovie =
    {
      id: movies.length + 1,
      title: title,
      description: title,
      posterUrl: url,
      rate: []
    }
    setMovies([...moviesList, newMovie])
    title = "";
    description = "";
    url = "";
    setAdd(!add)

  }

  // MAJ NOTE
  const rate = (id, i, [stars, setStars]) => {
    let x = stars.map((el, x) => (x <= i || el[i] === false) ? el = true : el = false);
    setStars(x);
    let y = x.filter((el) => el === true).length
    let newMovies = movies.map((movie, k) => id - 1 === k ? { ...movie, rate: movie.rate + y } : { ...movie })
    setMovies((prevMovies) => {
      return newMovies
    })
    setTimeout(() => {
      return setStars((pervState) => { return [false, false, false, false, false] })
    }, 2000)

  }
  // Search Fonction 
  const handleSearch = (search) => {
    const filtredList = movies.filter((movie) => movie.title.includes(search))
    setMovies(filtredList)
  }

  const check=(e)=>{
    console.log
  }

  return (
    <div className="app d-flex justify-content-start align-items-center">
      <div className="card text-white bg-dark mt-5" style={{ width: "1500px" }}>
        <div className="card-header d-flex align-items-center justify-content-between">
          <div className="d-flex d-row">
            <button type="button"
              className="btn btn-outline-dark border-0"
              data-toggle="modal"
              data-target="#exampleModal"
              onClick={AddMovieDetail} >
              <FontAwesomeIcon className="" color="gray" icon={faPlus} />
            </button>
            <button className="btn border-0 dropdown-toggle"  id="headerDropdown" type="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
              <FontAwesomeIcon classNameName="" color="gray" icon={faFilter} />
            </button>
            <div className="dropdown-menu bg-dark " aria-labelledby="headerDropdown">
              <div className="d-flex justify-content-center align-items-center my-3" >
                <input type="checkbox" aria-label="Checkbox for following text input"/>&emsp;
                <FontAwesomeIcon color="yellow "icon={faStar}/>
                <FontAwesomeIcon icon={faStar}/>
                <FontAwesomeIcon icon={faStar}/>
                <FontAwesomeIcon icon={faStar}/>
                <FontAwesomeIcon icon={faStar}/>
              </div>
              <div className="d-flex justify-content-center align-items-center my-3" >
                <input type="checkbox" aria-label="Checkbox for following text input"/>&emsp;
                <FontAwesomeIcon icon={faStar} color="yellow "/>
                <FontAwesomeIcon icon={faStar} color="yellow "/>
                <FontAwesomeIcon icon={faStar}/>
                <FontAwesomeIcon icon={faStar}/>
                <FontAwesomeIcon icon={faStar}/>
              </div>
              <div className="d-flex justify-content-center align-items-center my-3" >
                <input type="checkbox" aria-label="Checkbox for following text input"/>&emsp;
                <FontAwesomeIcon icon={faStar} color="yellow "/>
                <FontAwesomeIcon icon={faStar} color="yellow "/>
                <FontAwesomeIcon icon={faStar} color="yellow "/>
                <FontAwesomeIcon icon={faStar}/>
                <FontAwesomeIcon icon={faStar}/>
              </div>
              <div className="d-flex justify-content-center align-items-center my-3" >
                <input type="checkbox" aria-label="Checkbox for following text input"/>&emsp;
                <FontAwesomeIcon icon={faStar} color="yellow "/>
                <FontAwesomeIcon icon={faStar} color="yellow "/>
                <FontAwesomeIcon icon={faStar} color="yellow "/>
                <FontAwesomeIcon icon={faStar} color="yellow "/>
                <FontAwesomeIcon icon={faStar}/>
              </div>
              <div className="d-flex justify-content-center align-items-center my-3" >
                <input type="checkbox" aria-label="Checkbox for following text input" onChange={(e)=>check(e)}/>&emsp;
                <FontAwesomeIcon icon={faStar} color="yellow "/>
                <FontAwesomeIcon icon={faStar} color="yellow "/>
                <FontAwesomeIcon icon={faStar} color="yellow "/>
                <FontAwesomeIcon icon={faStar} color="yellow "/>
                <FontAwesomeIcon icon={faStar} color="yellow "/>
              </div>
            </div>
            {add ? <Modal add={add} saveDetailsMovie={saveDetailsMovie} /> : null}
          </div>
          <Filter handleSearch={handleSearch} />
        </div>
        <div className="card-body d-flex justify-content-between">
          <MoviesList movies={movies} rate={rate} />

        </div>
      </div>
    </div>
  );
}

export default App;
