import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar } from '@fortawesome/free-solid-svg-icons';
import './MoviesCards.css';

function MovieCard(props) {
  const [stars, setStars] = useState([false, false, false, false, false]);
  const [count, setCount] = useState(0);
  // conteur
  const handleCounteur = () => {
    setCount((prevCount) => {
      return prevCount + 1;
    });
  };
  const [show, setShow] = useState(false);
  const showDes = () => {
    setShow((prevState) => {
      return !prevState;
    });
  };

  const note = (props.movie.rate / count).toFixed(2);
  const alertContainer = (
    <div class="alert alert-warning alert-dismissible fade show" role="alert">
      <strong>Description: </strong>
      <br /> {props.movie.description}
      <button
        type="button"
        class="close"
        data-dismiss="alert"
        aria-label="Close"
        onClick={()=>showDes()}
      >
        <span aria-hidden="true">&times;</span>
      </button>
    </div>
  );

  return (
    <React.Fragment>
    {
      show? alertContainer:null
    }
    <div className="movie-container m-2">
      <img
        src={props.movie.posterUrl}
        className="card-img-top"
        alt="..."
        style={{ height: '75%' }}
        onClick={() => showDes()}

      />
      <div className="card-body">
        <h5
          className="card-title text-wrap text-center"
          style={{ fontSize: '17px' }}
        >
          {props.movie.title.toUpperCase()}
        </h5>
      </div>
      <div
        className="card-footer d-flex justify-content-between align-items-center"
        style={{ height: '60px' }}
      >
        <small className="text-muted">
          {stars.map((star, i) => (
            <FontAwesomeIcon
              key={i}
              color={star ? 'yellow' : 'gray'}
              size="lg"
              icon={faStar}
              onClick={() => {
                props.rate(props.movie.id, i, [stars, setStars]);
                handleCounteur();
              }}
            />
          ))}
        </small>
        <button type="button" className="btn btn-outline-secondary text-wrap">
          <span className="badge badge-light">{count === 0 ? 0 : note}</span> /
          5
        </button>
      </div>
    </div>
    </React.Fragment>
  );
}
export default MovieCard;
