import React,{useState} from 'react';

const AddMovie = () => {
  const [title,setTitle]=useState("");
  const [description,setDescription]=useState("");
  const [url,setUrl]=useState("");

  const titleValue=(e)=>{()=>{setTitle(e.target.value)},[]}

  const descriptionValue=(e)=>{

  }

  return (
    <div classname="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
      <div classname="modal-dialog" role="document">
        <div classname="modal-content bg-dark text-muted">
          <div classname="modal-header border-0">
            <h5 classname="modal-title" id="exampleModalLabel">Movie Details</h5>
            <button type="button" classname="close" data-dismiss="modal" aria-label="Close">
              <span aria-hidden="true">&times;</span>
            </button>
          </div>
          <div classname="modal-body">
            <form>
              <div classname="form-group">
                <label for="Title" classname="col-form-label">Title:</label>
                <input type="text" classname="form-control" id="Title" value={title} onChange={(e)=>titleValue(e)}/>
              </div>
              <div classname="form-group">
                <label for="Discription" classname="col-form-label">Description</label>
                <input type="textarea" classname="form-control" id="Discription" value={description} onChange={(e)=>descriptionValue(e)}/>/>
              </div>
              <div classname="form-group">
                <label for="exampleFormControlFile1">posterUrl:</label>
                <input type="file" classname="form-control-file rounded" value={url} id="exampleFormControlFile1"/>
              </div>
            </form>
          </div>
          <div classname="modal-footer border-0">
            <button type="button" classname="btn btn-secondary" data-dismiss="modal">Cancel</button>
            <button type="button" classname="btn btn-Success text-white">Save</button>
          </div>
        </div>
      </div>
    </div>

  );
};
export default AddMovie;
