import React,{useState}from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar } from '@fortawesome/free-solid-svg-icons'


function MovieCard(props) {

  const[count,setCount]=useState(1)
  const handleCounteur=()=>{
    setCount((prevCount)=>{
      return prevCount+1;
    });
  }

  const[stars,setStars]=useState([false,false,false,false,false])

  const note=(props.movie.rate/Math.abs(count-1)).toFixed(2)
  console.log(props.movie.rate,count)
  return (
    <div className="col mb-2">
      <div className="card bg-dark w-100 h-100">
        <img src={props.movie.posterUrl} className="card-img-top" alt="..." />
        <div className="card-body" style={{ height: '70px' }}>
          <h5
            className="card-title text-wrap text-center mt-1"
            style={{ fontSize: '17px' }}
          >
            {props.movie.title}
          </h5>
        </div>
        <div
          className="card-footer d-flex justify-content-between align-items-center"
          style={{ height: '60px' }}
        >
          <small className="text-muted">
            {
              stars.map((star,i)=>  <FontAwesomeIcon key={i} 
                                                    color={star ?"yellow":"gray"} 
                                                    size="lg" icon={faStar} 
                                                    onClick={()=> {
                                                                props.rate(props.movie.id,i,[stars,setStars]);
                                                                handleCounteur()
                                                                  }}/>)
            }
          </small>
          <button type="button" className="btn btn-outline-secondary text-wrap">
            <span className="badge badge-light">{count? 0 : note}</span> / 5</button>
        </div>
      </div>
    </div>
  );
}
export default MovieCard;
