import React,{useState} from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar } from '@fortawesome/free-solid-svg-icons'

const Rate =()=> {

    const[stars,setStars]=useState([false,false,false,false,false])
    const y=stars.filter((el)=>el===true).length;
    const [note,setNote]=useState(()=>{return  y})

    const rate=(i)=>{
        let x= stars.map((el,x)=> (x<=i || el[i]===false)? el=true:el=false);

        setNote((prevState)=>prevState+ x)
        console.log(note)
        setStars(x);
    }








    return (
        <div >
            {
                stars.map((star,i)=> star? <FontAwesomeIcon key={i} color="yellow" size="lg" icon={faStar} onClick={()=> rate(i)} />:<FontAwesomeIcon key={i} color="gray" size="lg" icon={faStar} onClick={()=> rate(i)} /> )
            }
        </div>
    )
}

export default Rate
