    const[stars,setStars]=useState([false,false,false,false,false])


    const rate=(i)=>{
        let x= stars.map((el,x)=> (x<=i || el[i]===false)? el=true:el=false);
        setStars(x);
    }









    return (
        <div >
            {
                stars.map((star,i)=>  <FontAwesomeIcon key={i} color={star ?"yellow":"gray"} size="lg" icon={faStar} onClick={()=> {rate(i)
                                                                                                                                    note
