import React ,{useState} from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus } from '@fortawesome/free-solid-svg-icons'


import './App.css';
import Filter from './Components/Filter Component/FilterComponent'
import MoviesList from './Components/List Movies Component/MoviesList' 
import Modal from './Components/Add Movie Component/AddMovie'


function App( props) {
  const moviesList=
  [
    {
      id:1,
      title:"OSEAM", 
      description:"", 
      posterUrl:"https://img0.cine974.com/images/affiches/grand/oseam-m6UcJ6nf.jpg", 
      rate:0,
    },
    {
      id:2,
      title:"THE GARDEN OF WORDS", 
      description:"", 
      posterUrl:"https://img0.cine974.com/images/affiches/grand/the-garden-of-words-ADDU4Lf6.jpg", 
      rate:0
    },
    {
      id:3,
      title:"LE GARÇON ET LA BÊTE", 
      description:"", 
      posterUrl:"https://img0.cine974.com/images/affiches/grand/le-gar--on-et-la-b--te-r1CYjwDX.jpg", 
      rate:0
    },{
      id:4,
      title:"OSEAM", 
      description:"", 
      posterUrl:"https://img0.cine974.com/images/affiches/grand/oseam-m6UcJ6nf.jpg", 
      rate:0
    },
    {
      id:5,
      title:"THE GARDEN OF WORDS", 
      description:"", 
      posterUrl:"https://img0.cine974.com/images/affiches/grand/the-garden-of-words-ADDU4Lf6.jpg", 
      rate:0
    },
    {
      id:6,
      title:"LE GARÇON ET LA BÊTE", 
      description:"", 
      posterUrl:"https://img0.cine974.com/images/affiches/grand/le-gar--on-et-la-b--te-r1CYjwDX.jpg", 
      rate:0
    }
  ]


  const [movies,setMovies] = useState(moviesList)
  const [add,setAdd]=useState(()=>{return false})
  const AddMovieDetail=()=>{
    setAdd(prevState=> !prevState)
  }
  // add Movie Details
  const saveDetailsMovie=(title,description,url)=>{

    const newMovie=
    {
      id:movies.length+1,
      title:title,
      description:title,
      posterUrl:url
    }
    setMovies([...moviesList,newMovie])
    title="";
    description="";
    url="";
    setAdd(!add)

  }


  const rate=(i,[stars,setStars])=>{
    let x= stars.map((el,x)=> (x<=i || el[i]===false)? el=true:el=false);
    setStars(x);
    // let y=x.filter((el)=> el===true).length
    let newMovies=movies.map((movie)=> console.log({...movie,rate:movie.rate+1}))
    console.log(newMovies)



  }






  return (
    <div className="app d-flex justify-content-center align-items-center">
      <div className="card text-white bg-dark mt-5" style={{ width: "1200px" }}>
        <div className="card-header d-flex align-items-center justify-content-between">
          <button type="button" 
                  className="btn btn-outline-dark border-0" 
                  data-toggle="modal" 
                  data-target="#exampleModal" 
                  onClick={AddMovieDetail} >
            <FontAwesomeIcon className="" color="gray" icon={faPlus}/>
          </button>    
          {add?  <Modal add={add} saveDetailsMovie={saveDetailsMovie}/>:null}     
          <Filter/>
        </div>
        <div className="card-body">
          <div className="row row-cols-1 row-cols-md-4">
            <MoviesList movies={movies} rate={rate} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
