import React,{useState} from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar } from '@fortawesome/free-solid-svg-icons'

const Rate =(props)=> {
    const[stars,setStars]=useState([false,false,false,false,false])

    const rate=(i)=>{
        let note= stars.map((el,x)=> x<=i? el=true:el=false)
        setStars(note); 
        console.log(note.filter((el)=>el===true).length)
    }




    return (
        <div >
            {
                stars.map((Star,i)=> <FontAwesomeIcon key={i} color={stars[i]===true? "yellow":"gray"} size="lg" icon={faStar} onClick={()=> rate(i)} />)
            }
        </div>
    )
}

export default Rate
