import React,{useState} from 'react';

const AddMovie = (props) => {
  const [title,setTitle]=useState("");
  const [description,setDescription]=useState("");
  const [url,setUrl]=useState("");

  const titleValue=(e)=>{setTitle(e.target.value)}
  const descriptionValue=(e)=>{setDescription(e.target.value)}
  const urlValue=(e)=>{setUrl(e.target.value)}

  return (
    <div className="modal fade" id="exampleModal" tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
      <div className="modal-dialog" role="document">
        <div className="modal-content bg-dark text-muted">
          <div className="modal-header border-0">
            <h5 className="modal-title" id="exampleModalLabel">Movie Details</h5>
            <button type="button" className="close" data-dismiss="modal" aria-label="Close">
              <span aria-hidden="true">&times;</span>
            </button>
          </div>
          <div className="modal-body">
            <form onSubmit={(e)=>e.preventDefault()}>
              <div className="form-group">
                <label htmlFor="Title" className="col-form-label">Title:</label>
                <input type="text" name="title"className="form-control" id="Title" value={title} onChange={handleChange}/>
              </div>
              <div className="form-group">
                <label htmlFor="Discription" className="col-form-label">Description</label>
                <input type="textarea" className="form-control" id="Discription" value={description} onChange={(e)=> descriptionValue(e)}/>
              </div>
              <div className="form-group">
                <label htmlFor="exampleFormControlFile1">posterUrl:</label>
                <input type="text" className="form-control rounded" id="exampleFormControlFile1"  value={url} onChange={(e)=>urlValue(e)}/>
              </div>
            </form>
          </div>
          <div className="modal-footer border-0">
            <button type="submit" className="btn btn-secondary" data-dismiss="modal">Cancel</button>
            <button type="submit" className="btn btn-Success text-white" onClick={()=>props.saveDetailsMovie(title,description,url)}>Save</button>
          </div>
        </div>
      </div>
    </div>

  );
};
export default AddMovie;
