import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar } from '@fortawesome/free-solid-svg-icons';

function MovieCard(props) {
  const [stars, setStars] = useState([false, false, false, false, false]);
  const [count, setCount] = useState(0);
  // conteur
  const handleCounteur = () => {
    setCount((prevCount) => {
      return prevCount + 1;
    });
  };

  const note = (props.movie.rate / count).toFixed(2);

  return (
    <React.Fragment>
      <div className="col mb-2">
        <div className="card bg-dark w-100 h-100">
          <img src={props.movie.posterUrl} className="card-img-top" alt="..." />
          <div className="card-body" style={{ height: '70px' }}>
            <h5
              className="card-title text-wrap text-center mt-1"
              style={{ fontSize: '17px' }}
            >
              {props.movie.title.toUpperCase()}
            </h5>
          </div>
          <div
            className="card-footer d-flex justify-content-between align-items-center"
            style={{ height: '60px' }}
          >
            <small className="text-muted">
              {stars.map((star, i) => (
                <FontAwesomeIcon
                  key={i}
                  color={star ? 'yellow' : 'gray'}
                  size="lg"
                  icon={faStar}
                  onClick={() => {
                    props.rate(props.movie.id, i, [stars, setStars]);
                    handleCounteur();
                  }}
                />
              ))}
            </small>
            <button
              type="button"
              className="btn btn-outline-secondary text-wrap"
            >
              <span className="badge badge-light">
                {count === 0 ? 0 : note}
              </span>{' '}
              / 5
            </button>
          </div>
        </div>
      </div>
      <div class="row">
        <div class="col-sm-12">
          <div class="card">
              <div class="card-body">
                <h5 class="card-title">Description</h5>
                <p class="card-text">With supporting text below as a natural lead-in to additional content.</p>

              </div>
            </div>
          </div>
        </div>
    </React.Fragment>
  );
}
export default MovieCard;
