              </div>
              <div className="form-group">
                <label htmlFor="Discription" className="col-form-label">Description</label>
                <input type="textarea" name="description" className="form-control" id="Discription" value={description} onChange={handleChange}/>
              </div>
              <div className="form-group">
                <label htmlFor="exampleFormControlFile1">posterUrl:</label>
                <input type="text"  name="url" className="form-control rounded" id="exampleFormControlFile1"  value={url} onChange={handleChange}/>
              </div>
            </form>
          </div>
          <div className="modal-footer border-0">
            <button type="submit" className="btn btn-secondary" data-dismiss="modal">Cancel</button>
            <button type="submit" className="btn btn-Success text-white" onClick={()=>props.saveDetailsMovie(title,description,url)}>Save</button>
          </div>
        </div>
      </div>
    </div>

  );
};
export default AddMovie;
