import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus } from '@fortawesome/free-solid-svg-icons'


import './App.css';
import Filter from './Components/Filter Component/FilterComponent'
import MoviesList from './Components/List Movies Component/MoviesList' 


function App( props) {
  const moviesList=
  [
    {
      id:1,
      title:"OSEAM", 
      description:"", 
      posterUrl:"https://img0.cine974.com/images/affiches/grand/oseam-m6UcJ6nf.jpg", 
      rate:""
    },
    {
      id:2,
      title:"THE GARDEN OF WORDS", 
      description:"", 
      posterUrl:"https://img0.cine974.com/images/affiches/grand/the-garden-of-words-ADDU4Lf6.jpg", 
      rate:""
    },
    {
      id:3,
      title:"LE GARÇON ET LA BÊTE", 
      description:"", 
      posterUrl:"https://img0.cine974.com/images/affiches/grand/le-gar--on-et-la-b--te-r1CYjwDX.jpg", 
      rate:""
    }
  ]

  const [movies,setMovies] = useState(moviesList)

  return (
    <div className="app d-flex justify-content-center align-items-center">
      <div className="card text-white bg-dark mt-5" style={{ width: "1200px" }}>
        <div className="card-header d-flex align-items-center justify-content-between">
        <button type="button" class="btn btn-outline-light border-0"><FontAwesomeIcon color="gray" icon={faPlus}/></button>
          <Filter/>

        </div>
        <div className="card-body d-flex d-wrap align-content-around"style={{height:"500px"}}>
          <MoviesList movies={movies}/>
        </div>
      </div>
    </div>
  );
}

export default App;
